'''
https://leetcode.com/problems/max-area-of-island/
695. Max Area of Island
'''
class Solution(object):
  def sinkAndSizeIsland(self, grid, row, col):
    sunkIslandSize = 0
    queue = [(row, col)]
    seen = set() # To avoid double counting
    while queue:
      current_row, current_col = queue.pop(0)
      grid[current_row][current_col] = 0
      sunkIslandSize += 1
      neighbours = [
        (current_row - 1, current_col),
        (current_row, current_col + 1),
        (current_row + 1, current_col),
        (current_row, current_col - 1),
      ]
      for neighbour_row, neighbour_col in neighbours:
        if 0 <= neighbour_row < len(grid) and \
          0 <= neighbour_col < len(grid[0]) and \
          grid[neighbour_row][neighbour_col] == 1 and \
          (neighbour_row, neighbour_col) not in seen:
          queue.append((neighbour_row, neighbour_col))
          seen.add((neighbour_row, neighbour_col))
    return sunkIslandSize

  def maxAreaOfIsland(self, grid):
    """
    :type grid: List[List[int]]
    :rtype: int
    """
    max_area = 0
    for row in range(len(grid)):
      for col in range(len(grid[0])):
        if grid[row][col] == 1:
          sunkIslandArea = self.sinkAndSizeIsland(grid, row, col)
          max_area = max(sunkIslandArea, max_area)
    return max_area

